    private void submitButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitButtonActionPerformed
        // TODO add your handling code here:
        String username = usernameTextField.getText();
        String password1 = (String) PasswordField1.getText();
        String password2 = (String) PasswordField2.getText();
        String name = nameTextField.getText();
        String feedback = null;

        if (password1.equals(password2)) {
            try {
                int updateQuery;
                Connection con = null;
                //Load JBBC driver "com.mysql.jdbc.Driver"
                Class.forName("com.mysql.jdbc.Driver");
                con = DriverManager.getConnection("jdbc:mysql://localhost:3306/library", "root", "");

                PreparedStatement statement = con.prepareStatement("INSERT INTO library.users (username, password, name) VALUES ('" + username + "', '" + password1 + "', '" + name + "')");
                updateQuery = statement.executeUpdate();
                if (updateQuery != 0) {
                    feedback = "New User Successfully Added";
                } else {
                    feedback = "submission unsuccessful 1";
                }

            } catch (Exception e) {
                System.out.println(e.getMessage());
                feedback = "submission unsuccessful ";
            }
            uLabel.setText(feedback);
        }

}//GEN-LAST:event_submitButtonActionPerformed

