    private void validButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_validButtonActionPerformed
        // TODO add your handling code here:
        String username = usernameTextField.getText();
        String feedback = null;

        try {
            ResultSet result = null;
            Connection con = null;
            //Load JBBC driver "com.mysql.jdbc.Driver"
            Class.forName("com.mysql.jdbc.Driver");
            con = DriverManager.getConnection("jdbc:mysql://localhost:3306/library", "root", "");

            PreparedStatement statement = con.prepareStatement("SELECT * FROM library.users WHERE username = '" + username + "'");
            result = statement.executeQuery();

            int i = 0;
            while (result.next()) {
                i += 1;
            }

            if (i == 0) {
                feedback = username + " is Valid";
                usernameTextField.setEditable(false);
                PasswordField1.setEditable(true);
                PasswordField2.setEditable(true);
                submitButton.setEnabled(true);
                nameTextField.setEditable(true);
            } else {
                feedback = username + " Already Exists, Try A Different Username";
            }

            result.close();
            con.close();

        } catch (Exception e) {
            System.out.println(e.getMessage());
        }
        validLabel.setText(feedback);

    }//GEN-LAST:event_validButtonActionPerformed

    private void changeButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_changeButtonActionPerformed
        // TODO add your handling code here:
        String username = TextFieldpassword.getText();
        String password1 = (String) PasswordField3.getText();
        String password2 = (String) PasswordField4.getText();
        String password3 = (String) PasswordField5.getText();
        String displayName = dnameTextField.getText();
        String password = null, feedback = null, id = null;

        try {
            ResultSet result = null;
            Connection con = null;
            //Load JBBC driver "com.mysql.jdbc.Driver"
            Class.forName("com.mysql.jdbc.Driver");
            con = DriverManager.getConnection("jdbc:mysql://localhost:3306/library", "root", "");

            PreparedStatement statement = con.prepareStatement("SELECT * FROM library.users WHERE username = '" + username + "'");
            result = statement.executeQuery();

            int i = 0;
            while (result.next()) {
                id = result.getString(1);
                password = result.getString(3);
                i += 1;
            }

            if (i == 0) {
                feedback = "Username Not Valid";
            } else {
                if (password.equals(password1)) {
                    if (!password2.equals("")) {
                        if (password2.equals(password3)) {
                            int updateQuery = 0;

                            PreparedStatement statement1 = con.prepareStatement("UPDATE library.users SET password = '" + password2 + "', name = '" + displayName + "' WHERE username = '" + username + "'");
                            updateQuery = statement1.executeUpdate();

                            if (updateQuery != 0) {
                                feedback = username + "'s Details Have Been Saved";
                            }
                        } else {
                            feedback = "Request Denied, Check New Password And Its Confirmation";
                        }
                    } else {
                        feedback = "Request Denied, New Password And Its Confirmation Should Not Be Blank";
                    }
                } else {
                    feedback = "Request Denied, Check Username & Current Password";
                }

            }

            result.close();
            con.close();

        } catch (Exception e) {
            System.out.println(e.getMessage());
        }
        changeLabel.setText(feedback);
        TextFieldpassword.setText("");
        PasswordField3.setText("");
        PasswordField4.setText("");
        PasswordField5.setText("");
        dnameTextField.setText("");




    }//GEN-LAST:event_changeButtonActionPerformed

